#class File_Functions(File):
class File():
    def mm2p(mm: int):
        return mm/0.352777
    
    def base64_images_decode(self,image):
        return io.BytesIO(base64.b64decode(image))

    def set_document_margins(self,document):
        sections = document.sections
        for section in sections:
            section.top_margin = Cm(3)
            section.right_margin = Cm(2)
            section.bottom_margin = Cm(2)
            section.left_margin = Cm(3)

    def set_document_font(self,document, font_style:str):
        style = document.styles['Normal']
        font = style.font
        font.name = font_style
        font.size = Pt(12)
    
    def month_name(self, month: int):
        if month == 1:
            return 'Janeiro'
        if month == 2:
            return 'Fevereito'
        if month == 3:
            return 'Mar√ßo'
        if month == 4:
            return 'Abril'
        if month == 5:
            return 'Maio'
        if month == 6:
            return 'Junho'
        if month == 7:
            return 'Julho'
        if month == 8:
            return 'Agosto'
        if month == 9:
            return 'Setembro'
        if month == 10:
            return 'Outubro'
        if month == 11:
            return 'Novembro'
        if month == 12:
            return 'Dezembro'
